import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getFirestore } from "firebase/firestore";
import { initializeAppCheck, ReCaptchaV3Provider } from "firebase/app-check";

// Arre, Firebase ka configuration le lo!
// Sab kuch environment variables se aa raha hai
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY, // API key - secure rakho!
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN, // Auth domain - auth ke liye
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID, // Project ID - unique hona chahiye
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET, // Storage bucket - files ke liye
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID, // Messaging ID - notifications ke liye
  appId: process.env.REACT_APP_FIREBASE_APP_ID, // App ID - app ka identity
  measurementId: process.env.REACT_APP_FIREBASE_MEASUREMENT_ID // Measurement ID - analytics ke liye
};

// Firebase ko initialize karo, bhai!
const app = initializeApp(firebaseConfig);

// App Check initialization - App Check token error ko fix karne ke liye
// Development mode mein debug token use karo
if (process.env.REACT_APP_ENVIRONMENT === "development") {
  // Development ke liye debug token enable karo
  if (typeof window !== "undefined") {
    window.FIREBASE_APPCHECK_DEBUG_TOKEN = process.env.REACT_APP_FIREBASE_APPCHECK_DEBUG_TOKEN;
  }
  console.log("ðŸ”§ Firebase App Check DEBUG mode enabled");
}

// App Check initialize karo agar reCAPTCHA key available hai
if (process.env.REACT_APP_RECAPTCHA_SITE_KEY) {
  try {
    initializeAppCheck(app, {
      provider: new ReCaptchaV3Provider(process.env.REACT_APP_RECAPTCHA_SITE_KEY),
      isTokenAutoRefreshEnabled: true // Token auto refresh karo
    });
    console.log("âœ“ Firebase App Check initialized with reCAPTCHA");
  } catch (error) {
    console.warn("App Check initialization warning:", error.message);
  }
}

// Auth aur Database export karo
export const auth = getAuth(app); // Login-logout ke liye
export const db = getFirestore(app); // Data store karne ke liye

// Auth state persistence
auth.setPersistence = (persistence) => {
  return auth.setPersistence(persistence).catch(error => {
    console.warn("Persistence error:", error);
  });
};

// Export app for other modules
export default app;
